import React, { useState } from "react";
import { PDFDocument } from "pdf-lib";

const Home = () => {
  const [pdfFileData, setPdfFileData] = useState();
  const [errorMessage, setErrorMessage] = useState("");
  const [selectedPages, setSelectedPages] = useState([]);
  const [pdfSrcDoc, setPdfSrcDoc] = useState(null);

  const readFileAsync = (file) => {
    return new Promise((resolve, reject) => {
      let reader = new FileReader();
      reader.onload = () => {
        resolve(reader.result);
      };
      reader.onerror = reject;
      reader.readAsArrayBuffer(file);
    });
  };

  const renderPdf = (uint8array) => {
    const tempblob = new Blob([uint8array], { type: "application/pdf" });
    const docUrl = URL.createObjectURL(tempblob);
    setPdfFileData(docUrl);
  };

  const extractPdfPage = async (arrayBuff) => {
    try {
      const loadedPdfSrcDoc = await PDFDocument.load(arrayBuff);
      setPdfSrcDoc(loadedPdfSrcDoc);

      console.log("Source PDF Document:", loadedPdfSrcDoc);
      console.log("Number of Pages in Source Document:", loadedPdfSrcDoc.getPageCount());

      const pdfNewDoc = await PDFDocument.create();
      
      if (!pdfNewDoc) {
        setErrorMessage("Error creating the new PDF document.");
        return;
      }

      const pageCount = loadedPdfSrcDoc.getPageCount();
      console.log("Number of Pages to Copy:", pageCount);

      selectedPages.forEach((pageIndex) => {
        if (pageIndex < pageCount) {
          const pageToCopy = loadedPdfSrcDoc.getPages()[pageIndex];
          console.log(`Copying page ${pageIndex}`);
          const copiedPage = pdfNewDoc.copyPages(loadedPdfSrcDoc, [pageToCopy])[0];
          console.log(`Adding copied page to new document`);
          pdfNewDoc.addPage(copiedPage);
        } else {
          console.error(`Page index ${pageIndex} is out of bounds.`);
        }
      });

      const newPdfData = await pdfNewDoc.save();
      console.log("New PDF Data Length:", newPdfData.length);
      renderPdf(newPdfData);
    } catch (error) {
      setErrorMessage("Error extracting or creating the new PDF file.");
      console.error("General error:", error);
    }
  };
  
  

  return (
    <>
      
      <input type="file" id="file-selector" accept=".pdf" onChange={onFileSelected} />
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
      <div>
        {pdfFileData && (
          <iframe
            style={{ display: "block", width: "100%", height: "500px" }}
            title="PdfFrame"
            src={pdfFileData}
            frameBorder="0"
            type="application/pdf"
          ></iframe>
        )}
        {pdfFileData && (
          <div>
            <h2>Select Pages:</h2>
            {Array.from({ length: pdfSrcDoc.getPageCount() }, (_, pageIndex) => (
              <label key={pageIndex}>
                <input
                  type="checkbox"
                  checked={selectedPages.includes(pageIndex)}
                  onChange={() => handleCheckboxChange(pageIndex)}
                />
                Page {pageIndex + 1}
              </label>
            ))}
            <button onClick={createNewPdfWithSelectedPages}>Create New PDF</button>
          </div>
        )}
      </div>
    </>
  );
};

export default Home;
