import { useState } from "react";
import { PDFDocument } from "pdf-lib";

export default function Home() {
  const [pdfFileData, setPdfFileData] = useState();

  async function readFileAsync(file) {
    try {
      const arrayBuffer = await file.arrayBuffer();
      if (arrayBuffer) {
        return new Uint8Array(arrayBuffer);
      } else {
        console.error("Error reading file: Array buffer is undefined.");
        return null;
      }
    } catch (error) {
      console.error("Error reading file:", error);
      return null;
    }
  }
  

  async function renderPdf(uint8array) {
    try {
      if (!uint8array) {
        throw new Error('Uint8Array is undefined or empty.');
      }
  
      const pdfDoc = await PDFDocument.load(uint8array);
  
      if (!pdfDoc) {
        throw new Error('PDFDocument is undefined.');
      }
  
      const [firstPage] = await pdfDoc.copyPages(pdfDoc, [0]);
      const newPdfDoc = await PDFDocument.create();
      newPdfDoc.addPage(firstPage);
  
      const newPdfBytes = await newPdfDoc.save();
  
      if (!newPdfBytes) {
        throw new Error('New PDF bytes are undefined.');
      }
  
      setPdfFileData(newPdfBytes);
    } catch (error) {
      console.error("Error rendering PDF:", error);
    }
  }
  

  // Execute when the user selects a file
  const onFileSelected = async (e) => {
    const fileList = e.target.files;
    if (fileList?.length > 0) {
      const pdfFile = fileList[0];
      const pdfArrayBuffer = await readFileAsync(pdfFile);
      renderPdf(pdfArrayBuffer);
    }
  };

  return (
    <>
      <h1>Hello world</h1>
      <input
        type="file"
        id="file-selector"
        accept=".pdf"
        onChange={onFileSelected}
      />
      <iframe
        title="PdfFrame"
        src={pdfFileData ? URL.createObjectURL(new Blob([pdfFileData], { type: "application/pdf" })) : null}
        frameBorder="0"
        type="application/pdf"
        style={{ display: "block", width: "100%", height: "90vh" }}
      ></iframe>
    </>
  );
}
