import { useState } from "react";
import { PDFDocument } from "pdf-lib";

function Home() {
  const [pdfFileData, setPdfFileData] = useState();

  

  const onFileSelected = async (e) => {
    const fileList = e.target.files;
    if (fileList?.length > 0) {
      const pdfFile = fileList[0];
      const pdfArrayBuffer = await readFileAsync(pdfFile);
      
      renderPdf(pdfArrayBuffer);
    }
  };

  return (
    <div>
      
      <input
        type="file"
        id="file-selector"
        accept=".pdf"
        onChange={onFileSelected}
      />
      <iframe
        title="PdfFrame"
        src={pdfFileData ? `data:application/pdf;base64,${pdfFileData.toString("base64")}` : null}
        frameBorder="0"
        type="application/pdf"
        style={{ display: "block", width: "100%", height: "90vh" }}
      ></iframe>
    </div>
  );
}

export default Home;
